INSERT INTO `wp_posts` VALUES(5, 1, '2013-12-09 10:11:15', '2013-12-09 10:11:15', '', 'image1_02', '', 'inherit', 'open', 'open', '', 'image1_02', '', '', '2013-12-09 10:11:15', '2013-12-09 10:11:15', '', 4, 'http://localhost:8888/elections/wp-content/uploads/2013/12/image1_02.png', 0, 'attachment', 'image/png', 0);
INSERT INTO `wp_posts` VALUES(6, 1, '2013-12-09 10:15:04', '2013-12-09 10:15:04', '<div class="home-container-outer">\r\n<p><strong>Y ELECTIONS ?</strong></p>\r\nWhy should I even bother with the elections?<br>\r\nMany young people ask themselves this very question when election time draws near, and this site aims to engage with young people to encourage them to participate in the 2014 Elections because a young person’s voice and action has the power to change society.<br> \r\nThis site will probe at the many perspectives we as young people have about our government, our country and the things we’d like to see instituted in South Africa.<br> \r\nYoung people are not complacent individuals who merely complain about things that are wrong in society, but rather they are people who are active, forward thinking and positive about South Africa.<br> \r\nWe are a generation that understands that many people from the previous generation sacrificed their lives so that we could experience democracy and freedom, and that it is therefore our responsibility to take care of this inheritance that we’ve been given and to continue running the good race because we are accountable to what happens to South Africa.<br> \r\n<!--more-->\r\n</div>', 'Home', '', 'publish', 'open', 'open', '', 'home', '', '', '2013-12-22 09:18:49', '2013-12-22 09:18:49', '', 0, 'http://localhost:8888/elections/?page_id=6', 0, 'page', '', 1);
